// src/components/CardExplorer.js

import React, { useState, useEffect } from 'react';

const CardExplorer = () => {
    const [cards, setCards] = useState([]);
    const [page, setPage] = useState(1);
    const [limit] = useState(10); // Fixed limit for simplicity
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchCards = async (page, limit) => {
        try {
            setLoading(true);
            const response = await fetch(`/api/cards/search?name=island&page=${page}&limit=${limit}`);
            const data = await response.json();
            setCards(data.data);
            setLoading(false);
        } catch (error) {
            setError('Error fetching cards');
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchCards(page, limit);
    }, [page, limit]);

    const handleNextPage = () => {
        setPage((prevPage) => prevPage + 1);
    };

    const handlePreviousPage = () => {
        setPage((prevPage) => Math.max(prevPage - 1, 1));
    };

    return (
        <div>
            <h1>Card Explorer</h1>
            {loading && <p>Loading...</p>}
            {error && <p>{error}</p>}
            <ul>
                {cards.map((card) => (
                    <li key={card.id}>{card.name}</li>
                ))}
            </ul>
            <button onClick={handlePreviousPage} disabled={page === 1}>
                Previous Page
            </button>
            <button onClick={handleNextPage}>Next Page</button>
        </div>
    );
};

export default CardExplorer;
