import React, { useState } from 'react';
import CardSearch from './components/CardSearch';
import CardList from './components/CardList';
import Deck from './components/Deck';
import PaginatedCards from './components/PaginatedCards';
import './App.css'; // Assuming you might want to keep your CSS

function App() {
    const [cards, setCards] = useState([]); // To hold the search results
    const [searchTerm, setSearchTerm] = useState('');

    const handleSearchResults = (results, term) => {
        setCards(results);
        setSearchTerm(term); // Update searchTerm state when search results are updated
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>Magic: The Gathering Deck Builder</h1>
                <CardSearch onSearchResults={handleSearchResults} />
                <CardList cards={cards} />
                <PaginatedCards searchTerm={searchTerm} /> {/* Pass searchTerm as a prop */}
                <Deck />
            </header>
        </div>
    );
}

export default App;
