// src/components/PaginatedCards.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CardList from './CardList';

function PaginatedCards() {
    const [cards, setCards] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [isLoading, setIsLoading] = useState(false);

    const fetchCards = async (page) => {
        setIsLoading(true);

        try {
            const response = await axios.get(`http://localhost:5001/api/cards?page=${page}&limit=10`);
            console.log('Cards received:', response.data.data);
            setCards(response.data.data); // Completely replaces the old data
            setIsLoading(false);
        } catch (error) {
            console.error('Failed to fetch cards:', error);
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchCards(currentPage);
    }, [currentPage]);

    return (
        <div>
            <CardList cards={cards} />
            {isLoading ? <p>Loading...</p> : null}
            <button onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))} disabled={currentPage === 1}>Previous</button>
            <button onClick={() => setCurrentPage(prev => prev + 1)}>Next</button>
        </div>
    );
}

export default PaginatedCards;
