// src/components/PaginatedCards.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CardList from './CardList';
import SearchBar from './SearchBar'; // Make sure this is the correct import for your search bar component

function PaginatedCards() {
    const [cards, setCards] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [isLoading, setIsLoading] = useState(false);
    const [searchTerm, setSearchTerm] = useState(''); // State to hold the search term

    const fetchCards = async (page) => {
        setIsLoading(true);
        try {
            const response = await axios.get(`http://localhost:5001/api/cards?page=${page}&limit=10&search=${searchTerm}`);
            setCards(response.data.data);
            setIsLoading(false);
        } catch (error) {
            console.error('Failed to fetch cards:', error);
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchCards(currentPage);
    }, [currentPage, searchTerm]); // Include searchTerm in the dependency array

    const handleSearch = (term) => {
        setSearchTerm(term);
        setCurrentPage(1); // Reset to the first page on a new search
    };

    return (
        <div>
            <SearchBar onSearch={handleSearch} />
            {isLoading ? <p>Loading...</p> : <CardList cards={cards} />}
            <button onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))} disabled={currentPage === 1}>Previous</button>
            <button onClick={() => setCurrentPage(prev => prev + 1)}>Next</button>
        </div>
    );
}

export default PaginatedCards;
