import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CardSearch({ onSearchResults }) {
    const [query, setQuery] = useState('');
    const [page, setPage] = useState(1);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const searchCards = async (page = 1) => {
        if (query.trim() === '') return;
        setLoading(true);
        setError(null);
        try {
            const response = await axios.get('http://localhost:5001/api/cards/search', {
                params: { name: query, page }
            });
            onSearchResults(response.data.data);
            setPage(response.data.page);
        } catch (err) {
            setError('Failed to fetch results. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleInputChange = (e) => {
        setQuery(e.target.value);
    };

    const handleSearch = () => {
        setPage(1); // Reset to the first page on new search
        searchCards(1);
    };

    const handleNextPage = () => {
        searchCards(page + 1);
    };

    useEffect(() => {
        if (page > 1) {
            searchCards(page);
        }
    }, [page]);

    return (
        <div>
            <input
                type="text"
                value={query}
                onChange={handleInputChange}
                placeholder="Search for cards..."
                style={{ padding: '5px', width: '200px' }}
            />
            <button onClick={handleSearch} disabled={loading}>
                {loading ? 'Searching...' : 'Search'}
            </button>
            {error && <div style={{ color: 'red' }}>{error}</div>}
            <button onClick={handleNextPage} disabled={loading || !query}>
                {loading ? 'Loading...' : 'Next'}
            </button>
        </div>
    );
}

export default CardSearch;
